import React, { useState } from 'react';
import UploadImage from './UploadImage';
import './App.css';

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [showResults, setShowResults] = useState(false);

  return (
    <div className="App">
      {!showResults ? (
        <div className="upload-container">
          <h2>Upload an Image</h2>
          <UploadImage onUpload={setUploadedImage} />
          {uploadedImage && (
            <button className="process-btn" onClick={() => setShowResults(true)}>
              Process Image
            </button>
          )}
        </div>
      ) : (
        <div className="results-container">
          <h2>Analysis Result</h2>
          <img src={uploadedImage} alt="Processed" className="result-image" />
          <p><strong>Status:</strong> Analyzing Hydration Levels...</p>
          <button className="back-btn" onClick={() => setShowResults(false)}>
            Upload Another Image
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
