import React, { useState } from 'react';

function UploadImage({ onUpload }) {
  const [imagePreview, setImagePreview] = useState(null);
  const [dragging, setDragging] = useState(false);

  const handleImageUpload = (e) => {
    let file = e.target.files[0] || e.dataTransfer.files[0];
    if (file) {
      const fileURL = URL.createObjectURL(file);
      setImagePreview(fileURL);
      onUpload(fileURL);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragging(true);
  };

  const handleDragLeave = () => setDragging(false);

  const handleDrop = (e) => {
    e.preventDefault();
    setDragging(false);
    handleImageUpload(e);
  };

  return (
    <div 
      className={`upload-box ${dragging ? 'dragging' : ''}`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      <input type="file" accept="image/*" onChange={handleImageUpload} hidden id="fileUpload" />
      <label htmlFor="fileUpload" className="upload-label">
        {imagePreview ? (
          <img src={imagePreview} alt="Uploaded Preview" className="preview" />
        ) : (
          <p>ðŸ“‚ Drag & Drop an image or <span>Click to Upload</span></p>
        )}
      </label>
    </div>
  );
}

export default UploadImage;
